.iiif-av-component .options-container {
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  margin: 0;
  padding: 0 2.5% 0 2.5%;
  background: linear-gradient(0deg, rgba(50,50,50,0.75) 0%, rgba(29,29,29,0) 100%);
}

.iiif-av-component .canvas-timeline-container {
  width: auto !important;
}

.iiif-av-component .loading-progress {
  position: absolute;
  height: 5px;
  background-color: #CCCCCC;
  box-shadow: inset 0 -1px 0 0 rgba(255,255,255,0.15), inset 0 1px 0 0 rgba(255,255,255,0.15);
}

.btn[title=Previous] {
  display: none;
}

.btn[title=Next] {
  display: none;
}

.btn[title=Fullscreen] {
  float: right !important;
}

.btn[title=More] {
  float: right !important;
}

.btn[title=Subtitles] {
  display: none;
  float: right !important;
}

.moremenu {
  display: none;
  font-size: 14px !important;
  color: #EDEDED !important;
  background-color: rgba(0,0,0,0.8) !important;
  font-weight: 600 !important;
  margin-bottom: 10px !important;
  padding: 10px !important;
  z-index: 100;
  text-align: left;
}

.moremenu-option {
  cursor: pointer;
}

.subtitlemenu {
  display: none;
  font-size: 14px !important;
  color: #EDEDED !important;
  background-color: rgba(0,0,0,0.8) !important;
  font-weight: 600 !important;
  margin-bottom: 10px !important;
  padding: 10px !important;
  z-index: 100;
  text-align: left;
}

.subtitlemenu-option {
  cursor: pointer;
